        [TestMethod]
        public void WellboreCompletion141DataAdapter_ChangeLog_Tracks_ChangeHistory_For_Add_Update_Delete()
        {
            AddParents();

            // Add the WellboreCompletion141
            DevKit.AddAndAssert<WellboreCompletionList, WellboreCompletion>(WellboreCompletion);

            // Verify ChangeLog for Add
            var result = DevKit.GetAndAssert<WellboreCompletionList, WellboreCompletion>(WellboreCompletion);
            var expectedHistoryCount = 1;
            var expectedChangeType = ChangeInfoType.add;
            DevKit.AssertChangeLog(result, expectedHistoryCount, expectedChangeType);

            // Update the WellboreCompletion141
            WellboreCompletion.Name = "Change";
            DevKit.UpdateAndAssert(WellboreCompletion);

            result = DevKit.GetAndAssert<WellboreCompletionList, WellboreCompletion>(WellboreCompletion);
            expectedHistoryCount = 2;
            expectedChangeType = ChangeInfoType.update;
            DevKit.AssertChangeLog(result, expectedHistoryCount, expectedChangeType);

            // Delete the WellboreCompletion141
            DevKit.DeleteAndAssert(WellboreCompletion);

            expectedHistoryCount = 3;
            expectedChangeType = ChangeInfoType.delete;
            DevKit.AssertChangeLog(WellboreCompletion, expectedHistoryCount, expectedChangeType);

            // Re-add the same WellboreCompletion141...
            DevKit.AddAndAssert<WellboreCompletionList, WellboreCompletion>(WellboreCompletion);

            //... the same changeLog should be reused.
            result = DevKit.GetAndAssert<WellboreCompletionList, WellboreCompletion>(WellboreCompletion);
            expectedHistoryCount = 4;
            expectedChangeType = ChangeInfoType.add;
            DevKit.AssertChangeLog(result, expectedHistoryCount, expectedChangeType);

            DevKit.AssertChangeHistoryTimesUnique(result);
        }

        [TestMethod]
        public void WellboreCompletion141DataAdapter_GetFromStore_Filter_ExtensionNameValue()
        {
            AddParents();

            var extensionName1 = DevKit.ExtensionNameValue("Ext-1", "1.0", "m");
            var extensionName2 = DevKit.ExtensionNameValue("Ext-2", "2.0", "cm", PrimitiveType.@float);
            extensionName2.MeasureClass = MeasureClass.Length;
            var extensionName3 = DevKit.ExtensionNameValue("Ext-3", "3.0", "cm", PrimitiveType.unknown);

            WellboreCompletion.CommonData = new CommonData()
            {
                ExtensionNameValue = new List<ExtensionNameValue>()
                {
                    extensionName1, extensionName2, extensionName3
                }
            };

            // Add the WellboreCompletion141
            DevKit.AddAndAssert(WellboreCompletion);

            // Query for first extension
            var commonDataXml = "<commonData>" + Environment.NewLine +
                                "<extensionNameValue uid=\"\">" + Environment.NewLine +
                                "<name />{0}" + Environment.NewLine +
                                "</extensionNameValue>" + Environment.NewLine +
                                "</commonData>";

            var extValueQuery = string.Format(commonDataXml, "<dataType>double</dataType>");
            var queryXml = string.Format(BasicXMLTemplate, WellboreCompletion.UidWell, WellboreCompletion.UidWellbore, WellboreCompletion.Uid, extValueQuery);
            var result = DevKit.Query<WellboreCompletionList, WellboreCompletion>(ObjectTypes.WellboreCompletion, queryXml, null, OptionsIn.ReturnElements.Requested);
            Assert.IsNotNull(result);
            Assert.AreEqual(1, result.Count);

            var resultWellboreCompletion = result[0];
            Assert.IsNotNull(resultWellboreCompletion);

            var commonData = resultWellboreCompletion.CommonData;
            Assert.IsNotNull(commonData);
            Assert.AreEqual(1, commonData.ExtensionNameValue.Count);

            var env = commonData.ExtensionNameValue[0];
            Assert.IsNotNull(env);
            Assert.AreEqual(extensionName1.Uid, env.Uid);
            Assert.AreEqual(extensionName1.Name, env.Name);

            // Query for second extension
            extValueQuery = string.Format(commonDataXml, "<measureClass>length</measureClass>");
            queryXml = string.Format(BasicXMLTemplate, WellboreCompletion.UidWell, WellboreCompletion.UidWellbore, WellboreCompletion.Uid, extValueQuery);
            result = DevKit.Query<WellboreCompletionList, WellboreCompletion>(ObjectTypes.WellboreCompletion, queryXml, null, OptionsIn.ReturnElements.Requested);
            Assert.IsNotNull(result);
            Assert.AreEqual(1, result.Count);

            resultWellboreCompletion = result[0];
            Assert.IsNotNull(resultWellboreCompletion);

            commonData = resultWellboreCompletion.CommonData;
            Assert.IsNotNull(commonData);
            Assert.AreEqual(1, commonData.ExtensionNameValue.Count);

            env = commonData.ExtensionNameValue[0];
            Assert.IsNotNull(env);
            Assert.AreEqual(extensionName2.Uid, env.Uid);
            Assert.AreEqual(extensionName2.Name, env.Name);

            // Query for third extension
            extValueQuery = string.Format(commonDataXml, "<dataType>unknown</dataType>");
            queryXml = string.Format(BasicXMLTemplate, WellboreCompletion.UidWell, WellboreCompletion.UidWellbore, WellboreCompletion.Uid, extValueQuery);
            result = DevKit.Query<WellboreCompletionList, WellboreCompletion>(ObjectTypes.WellboreCompletion, queryXml, null, OptionsIn.ReturnElements.Requested);
            Assert.IsNotNull(result);
            Assert.AreEqual(1, result.Count);

            resultWellboreCompletion = result[0];
            Assert.IsNotNull(resultWellboreCompletion);

            commonData = resultWellboreCompletion.CommonData;
            Assert.IsNotNull(commonData);
            Assert.AreEqual(1, commonData.ExtensionNameValue.Count);

            env = commonData.ExtensionNameValue[0];
            Assert.IsNotNull(env);
            Assert.AreEqual(extensionName3.Uid, env.Uid);
            Assert.AreEqual(extensionName3.Name, env.Name);
        }

